import styled from "styled-components";
import { BraintreeHostedFields } from 'braintree-web-react';
import FormValidator from '../formValidator'

const Picture = styled.img`
border-radius: 50%;
border: 3px solid white;
width: 100px;
`;


class ProfileEdit extends React.Component {
    constructor(props) {
        super(props);

        this.submitted = false;

        this.validator = new FormValidator
            (
                [
                    {
                        field: 'email',
                        method: 'isEmpty',
                        validWhen: false,
                        message: 'Email is required.'
                    },
                    {
                        field: 'email',
                        method: 'isEmail',
                        validWhen: true,
                        message: 'That is not a valid email.'
                    },
                    {
                        field: 'name',
                        method: 'isEmpty',
                        validWhen: 'false',
                        message: 'Please enter your name'
                    },
                ]
            )
        this.state =
            {
                clientToken: null,
                email: this.props.user.email,
                payment: this.props.user.user_metadata,
                picture: this.props.user.picture,
                description: this.props.user.user_metadata,
                name: this.props.user.name,
                validation: this.validator.valid(),
            };


        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (e) => {
        e.preventDefault();
        this.setState({ [e.target.name]: e.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();

        const validation = this.validator.validate(this.state);
        this.setState({ validation });
        this.submitted = true;

        if (validation.isValid) {

            fetch('https://dev-luqm6uty.auth0.com/api/v2/users/${this.props.user.user_id}',
                {
                    method: 'POST',
                    headers:
                    {
                        'Content-Type': 'application/json',
                        'authorization': 'Bearer ' + "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6Ik5FUkRPREE1TlRCRk5UTkdRMFUwTVRVek1qaEZPVVF3T0RkRFFUTTVOa1ZGTlRaRk56azVSZyJ9..Iakp1I4gf8epO8AJiAjuMLTMv5sfcPNqIPR3juEjaolsUbB2XFQPUlKnztQsQ8aD_lAfmEVe0Prg3umLszM0LVXXEBWHhPka4lGQD6KWJazEX1_iNscjFFyRRrR5PFBC4tGELJ3O3WmC4XO6-TdzdTUllU99Nnx3tkV_LFOsijDnDeAAaMAcnp658NlVQip2Eh7_b6gHyXgli-oGjnE9r4Zkkqh4pFxfZFcKS-9QCCfvud7fFRa0j917ay6_IVYXye_5DbuLbgNQtoubGcGPmNYLNpj9jNFuY39fDb_kpNh4zhoIKlo0nc4aJSU6mXisxBKbL-H_Nqj4sujcPlXStQ"
                    },
                    body: {
                        "email": this.state.email,
                        "user_metadata": {
                            'braintree': this.state.payment,
                            'description': this.state.description,
                        },
                        'name': this.state.name,
                        'picture': this.state.picture,

                    },
                    credentials: "include"
                }).then(console.log('tried'))

        }

    }

    async componentDidMount() {

        try {
            // Get a client token for authorization from your server
            const response = await fetch('http://localhost:8000/api/braintree/v1/getToken', { method: 'GET' })
            const clientToken = response.data.clientToken

            this.setState({ clientToken })

        } catch (err) {
            console.error(err)
        }
    }

    render() {
        let validation = this.submitted ?
            this.validator.validate(this.state) :
            this.state.validation

        return (
            <div class='container'>
                <form onSubmit={this.handleSubmit}>
                    <div class="profile">
                        <div class="photo">
                            <input type="file" accept="image/*" />
                            <div class="photo__helper">
                                <div class="photo__frame photo__frame--circle">
                                    <canvas class="photo__canvas"></canvas>
                                    <div class="message is-empty">
                                        <p class="message--desktop">Drop your photo here or browse your computer.</p>
                                        <p class="message--mobile">Tap here to select your picture.</p>
                                    </div>
                                    <div class="message is-loading">
                                        <i class="fa fa-2x fa-spin fa-spinner"></i>
                                    </div>
                                    <div class="message is-dragover">
                                        <i class="fa fa-2x fa-cloud-upload"></i>
                                        <p>Drop your photo</p>
                                    </div>
                                    <div class="message is-wrong-file-type">
                                        <p>Only images allowed.</p>
                                        <p class="message--desktop">Drop your photo here or browse your computer.</p>
                                        <p class="message--mobile">Tap here to select your picture.</p>
                                    </div>
                                    <div class="message is-wrong-image-size">
                                        <p>Your photo must be larger than 350px.</p>
                                    </div>
                                </div>
                            </div>

                            <div class="photo__options hide">
                                <div class="photo__zoom">
                                    <input type="range" class="zoom-handler" />
                                </div>
                            </div>
                        </div>
                    </div>
                    <label>Profile Picture:</label>
                    <button onClick={(e) => { }}><Picture src={this.state.picture} alt={this.state.name} /> </button>

                    <label>Name:</label>
                    <input type='text' placeholder='Name' value={this.state.name} onChange={this.handleChange} />

                    <label>Email:</label>
                    <input type='email' placeholder='Email' value={this.state.email} onChange={this.handleChange} />

                    <label> Description: </label>
                    <input type='text' placeholder='Tell us about yourself ' value={this.state.description} onChange={this.handleChange} />

                    <label>Subjects Taught:</label>

                    <label> Payment Info: </label>
                    <BraintreeHostedFields
                        className="drop-in-container"
                        options={{
                            authorization: this.state.clientToken
                        }}
                        onInstance={(instance) => (this.instance = instance)}
                    >
                        <form id="cardForm">
                            <label className="hosted-fields--label">Card Number</label>
                            <div id="card-number" className="hosted-field"></div>

                            <label className="hosted-fields--label">Expiration Date</label>
                            <div id="expiration-date" className="hosted-field"></div>

                            <label className="hosted-fields--label">CVV</label>
                            <div id="cvv" className="hosted-field"></div>

                            <label className="hosted-fields--label">Postal Code</label>
                            <div id="postal-code" className="hosted-field"></div>
                        </form>
                    </BraintreeHostedFields>

                    <input class="btn btn-dark" type='submit' value='Save Changes' />

                </form>
                <style jsx>{`
            /*DEMO*/
            .preview {
              margin: 10px;
              display: none;
            }
            .preview--rounded {
              width: 160px;
              height: 160px;
              border-radius: 50%;
            }
            /* IMMUTABLE */
            .hide {
              display: none !important;
            }
            * {
              box-sizing: border-box;
            }
            .photo__zoom {
              position: relative;
              padding-left: 22px;
              padding-right: 22px;
            /**
                * Zoom
                */
            /**
                * Zoom handler
                */
            /**
                * FOCUS
                */
            /**
                * Zoom track
                */
            /**
                * ICONS
                */
            }
            .photo__zoom input[type=range] {
              -webkit-appearance: none;
              width: 100%;
              background: transparent;
              height: 18px;
            }
            .photo__zoom input[type=range]::-webkit-slider-thumb {
              -webkit-appearance: none;
            }
            .photo__zoom input[type=range]:focus {
              outline: none;
            }
            .photo__zoom input[type=range]::-ms-track {
              width: 100%;
              cursor: pointer;
              background: transparent;
              border-color: transparent;
              color: transparent;
            }
            .photo__zoom input[type=range]:focus::-ms-thumb {
              border-color: #268eff;
              box-shadow: 0 0 1px 0px #268eff;
            }
            .photo__zoom input[type=range]:focus::-moz-range-thumb {
              border-color: #268eff;
              box-shadow: 0 0 1px 0px #268eff;
            }
            .photo__zoom input[type=range]:focus::-webkit-slider-thumb {
              border-color: #268eff;
              box-shadow: 0 0 1px 0px #268eff;
            }
            .photo__zoom input[type=range]::-webkit-slider-thumb {
              -webkit-appearance: none;
              margin-top: -9px;
              box-sizing: border-box;
              cursor: pointer;
              width: 18px;
              height: 18px;
              display: block;
              border-radius: 50%;
              background: #eee;
              border: 1px solid #ddd;
            }
            .photo__zoom input[type=range]::-webkit-slider-thumb:hover {
              border-color: #c1c1c1;
            }
            .photo__zoom input[type=range]::-ms-thumb {
              margin-top: 0;
              box-sizing: border-box;
              cursor: pointer;
              width: 18px;
              height: 18px;
              display: block;
              border-radius: 50%;
              background: #eee;
              border: 1px solid #ddd;
            }
            .photo__zoom input[type=range]::-ms-thumb:hover {
              border-color: #c1c1c1;
            }
            .photo__zoom input[type=range]::-moz-range-thumb {
              margin-top: 0;
              box-sizing: border-box;
              cursor: pointer;
              width: 18px;
              height: 18px;
              display: block;
              border-radius: 50%;
              background: #eee;
              border: 1px solid #ddd;
            }
            .photo__zoom input[type=range]::-moz-range-thumb:hover {
              border-color: #c1c1c1;
            }
            .photo__zoom input[type=range]::-webkit-slider-runnable-track {
              width: 100%;
              height: 1px;
              cursor: pointer;
              background: #eee;
              border: 0;
            }
            .photo__zoom input[type=range]::-moz-range-track {
              width: 100%;
              height: 1px;
              cursor: pointer;
              background: #eee;
              border: 0;
            }
            .photo__zoom input[type=range]::-ms-track {
              width: 100%;
              height: 1px;
              cursor: pointer;
              background: #eee;
              border: 0;
            }
            .photo__zoom input[type=range].zoom--minValue::before,
            .photo__zoom input[type=range].zoom--maxValue::after {
              color: #f8f8f8;
            }
            .photo__zoom input[type=range]::before,
            .photo__zoom input[type=range]::after {
              position: absolute;
              content: "\f03e";
              display: block;
              font-family: 'FontAwesome';
              color: #aaa;
              transition: color 0.3s ease;
            }
            .photo__zoom input[type=range]::after {
              font-size: 18px;
              right: -2px;
              top: 2px;
            }
            .photo__zoom input[type=range]::before {
              font-size: 14px;
              left: 4px;
              top: 4px;
            }
            /**
            * FRAME STYLE
            */
            .photo__frame--circle {
              border: 1px solid #e2e2e2;
              border-radius: 50%;
            }
            .photo__helper {
              position: relative;
              background-repeat: no-repeat;
              background-color: transparent;
              padding: 15px 0;
            }
            .photo__helper .canvas--helper {
              position: absolute;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
            }
            .photo__frame img,
            .photo__helper {
              -webkit-touch-callout: none;
              -webkit-user-select: none;
              -khtml-user-select: none;
              -moz-user-select: none;
              -ms-user-select: none;
              user-select: none;
            }
            .profile {
              position: relative;
              font-family: 'HelveticaNeueLTPro-Roman', sans-serif;
              font-size: 85%;
              width: 300px;
            }
            .photo {
              text-align: center;
              margin-bottom: 15px;
            }
            .photo input[type=file] {
              display: none;
            }
            .photo__options {
              margin-top: 15px;
              position: relative;
              text-align: left;
            }
            .photo__options .remove {
              padding: 0;
              padding: 0;
              display: inline-block;
              text-decoration: none;
              color: #ddd;
              font-size: 18px;
              width: 20%;
              text-align: center;
              vertical-align: middle;
            }
            .photo__options .remove:hover {
              color: #000;
            }
            .photo__zoom {
              vertical-align: middle;
              width: 80%;
              display: inline-block;
            }
            .photo__frame {
              cursor: move;
              overflow: hidden;
              position: relative;
              display: inline-block;
              width: 160px;
              height: 160px;
            }
            .photo__frame img,
            .photo__helper img {
              position: relative;
            }
            .photo__frame .message {
              position: absolute;
              left: 5px;
              right: 5px;
              top: 50%;
              transform: translateY(-50%);
              display: inline-block;
              color: #268eff;
              z-index: 3;
            }
            .photo__frame .is-dragover {
              display: none;
            }
            .message p {
              font-size: 0.9em;
            }
            .photo__options {
              list-style: none;
            }
            .photo__options li {
              display: inline-block;
              text-align: center;
              width: 50%;
            }
            .photo--empty .photo__frame {
              cursor: pointer;
            }
            /**
            * IMG states
            */
            .profile.is-dragover .photo__frame img,
            .photo--empty img,
            .photo--error img,
            .photo--error--file-type img,
            .photo--error--image-size img,
            .photo--loading img {
              display: none;
            }
            /**
            * States
            */
            /** SELECT PHOTO MESSAGE */
            .message--desktop,
            .message--mobile {
              display: none;
            }
            /* MOBILE */
            .is-mobile .message--mobile {
              display: inline-block;
            }
            .is-mobile .message--desktop {
              display: none;
            }
            /* DESKTOP */
            .is-desktop .message--desktop {
              display: inline-block;
            }
            .is-desktop .message--mobile {
              display: none;
            }
            /* DEFAULT */
            .message.is-empty,
            .message.is-loading,
            .message.is-wrong-file-type,
            .message.is-wrong-image-size,
            .message.is-something-wrong,
            .message.is-dragover {
              display: none;
            }
            /* EMPTY */
            .photo--empty .photo__options {
              display: none;
            }
            .photo--empty .message.is-empty {
              display: inline-block;
            }
            .photo--empty .photo__frame:hover {
              background: #268eff;
            }
            .photo--empty .photo__frame:hover .message {
              color: #fff;
            }
            /* LOADING */
            .photo--loading .message.is-loading {
              display: inline-block;
            }
            .photo--loading .message.is-empty,
            .photo--loading .message.is-wrong-file-type,
            .photo--loading .message.is-dragover,
            .photo--loading .message.is-wrong-image-size,
            .photo--loading .photo__options {
              display: none;
            }
            /* ERROR */
            /* UNKNOWN */
            .photo--error .message.is-empty,
            .photo--error .message.is-loading,
            .photo--error .message.is-dragover,
            .photo--error .message.is-wrong-image-size,
            .photo--error .photo__options {
              display: none;
            }
            .photo--error .message.is-something-wrong {
              display: inline-block;
            }
            /* FILE TYPE*/
            .photo--error--file-type .message.is-empty,
            .photo--error--file-type .message.is-loading,
            .photo--error--file-type .message.is-dragover,
            .photo--error--file-type .message.is-wrong-image-size,
            .photo--error--file-type .photo__options {
              display: none;
            }
            .photo--error--file-type .message.is-wrong-file-type {
              display: inline-block;
            }
            /* IMAGE SIZE */
            .photo--error--image-size .message.is-empty,
            .photo--error--image-size .message.is-loading,
            .photo--error--image-size .message.is-dragover,
            .photo--error--image-size .message.is-wrong-file-type,
            .photo--error--image-size .photo__options {
              display: none;
            }
            .photo--error--image-size .message.is-wrong-image-size {
              display: inline-block;
            }
            /* DRAGOVER */
            .profile.is-dragover .photo__frame .is-dragover {
              display: inline-block;
            }
            .profile.is-dragover .message.is-empty,
            .profile.is-dragover .message.is-loading,
            .profile.is-dragover .message.is-wrong-file-type,
            .profile.is-dragover .message.is-wrong-image-size {
              display: none;
            }
                      `}</style>
            </div>
      
        );
    }


}

export default ProfileEdit;
