import React from "react";
import App, { Container as NextContainer } from "next/app";
import Head from "next/head";
//import Container from "react-bootstrap/Container";
//import Jumbotron from "react-bootstrap/Jumbotron";
import Navbar from "../components/Navbar";
import fetch from 'isomorphic-fetch'

class MyApp extends App {
 
  static async getInitialProps({ Component, ctx }) 
  {
    
    let pageProps = {};
    if (Component.getInitialProps) 
    {
      pageProps = await Component.getInitialProps(ctx);
    }
    if (ctx.req && ctx.req.session.passport)
    {
      pageProps.user = await ctx.req.session.passport.user;
      
    }
 
    var bearer = 'Bearer ' + "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6Ik5FUkRPREE1TlRCRk5UTkdRMFUwTVRVek1qaEZPVVF3T0RkRFFUTTVOa1ZGTlRaRk56azVSZyJ9..hHcZ45TqSpqvBGZSIWPzdfsvRqSG_oXZ0D2ebjq9OIAGAzIMeN4_nambcDmle-z29OgJETiaT7etP-Vg5IZMXPlwzbYRt99pa_mWdVubrU8SVD4vic9RpOGF1mJ54mzkf9gcN12wRWdjLKdzCz0AJvetpoDWMiODmaAuhWUImh62rmKjFvOTW-f-LL6tZAymm_mu8eME5L2JSNrGt8eLtA7S3knXS5CLgPsa7cefmLKtbQ79UM2DMLQtrE3H43reR9bNjRqsu7JdQffAPgMB1ozDHcmiIrSVPhLyEjZVz-aOxqoPtMdI89wdRt_G140kNvixbZG_Bc669oKkQiCDwg";
    var fuser = 'https://dev-luqm6uty.auth0.com/api/v2/users/'
    fuser += await pageProps.user.user_id
      fetch(fuser, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'authorization': bearer
        },
        credentials: "include"
      })
      .then((response) => response.json() )
      .then( function(jsonr) {
        if(jsonr.status >= 400)
        {
          throw new Error('Bad Response from server: ' + jsonr.status + ' ' + jsonr.statusText)
        }
      
       pageProps.user = jsonr;
       //console.log(pageProps.user)
      })

    return { pageProps };
  
  }

  constructor(props) {
    super(props);
    console.log(props.pageProps.user)
    this.state = {
      user: props.pageProps.user
    };
  }

  render() {
    const { Component, pageProps } = this.props;

    const props = {
      ...pageProps,
      user: this.state.user,
    };

    return (
      <NextContainer>
        <Head>
          <title>TutorKwik | Study with a Button</title>
          <link rel="stylesheet" href="https://bootswatch.com/4/lux/bootstrap.min.css"/>
          <meta name="viewport" content="width=device-width, initial-scale=1"/>   
        </Head>
        <Navbar user={this.state.user} />
            <Component {...props} />
      </NextContainer>
    );
  }
}

export default MyApp;
