import React from "react";
import App, { Container as NextContainer } from "next/app";
import Head from "next/head";
//import Container from "react-bootstrap/Container";
//import Jumbotron from "react-bootstrap/Jumbotron";
import Navbar from "../components/Navbar";
import fetch from 'isomorphic-fetch'

import '../static/css/empty.css'


class MyApp extends App {
 
  static async getInitialProps({ Component, ctx }) 
  {
    
    let pageProps = {};
    if (Component.getInitialProps) 
    {
      pageProps = await Component.getInitialProps(ctx);
    }
    
    if (ctx.req && ctx.req.session.passport)
    {
      pageProps.user = ctx.req.session.passport.user

      var bearer = 'Bearer ' + "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6Ik5FUkRPREE1TlRCRk5UTkdRMFUwTVRVek1qaEZPVVF3T0RkRFFUTTVOa1ZGTlRaRk56azVSZyJ9..X4yHzULntm8SLMOhIAn8oKRhCqFM7iy0DpTBS2Dz6RPwK8F0MKdYwTBPlmzzQtwRR9pt-3ewdtz5OzZRBGoD7mgzorNyKbOroGuXdYNvVutBbdWKu3cIjfEQDU_7s6N4GmT7XZzI36u_4vyRr6chCXz9RCxvupFtP0UMx5OYhIeWeuaqBh43nSjlNd5xWohkXcHTYj6XPrMH73DHN_ZhmdqPDs8j1I9XWwrjlV_Fu034DXhgka4QHu5yvpE2ftuR2VhNsyrDAU3Aep9S1DHOUUdS4eZmyx-rhLqI49U4B6D8opmxnLwGA67Iqh-HgEsx3Gbx5iZ_jkD32J2ABzY7Pg";
      var fuser = 'https://dev-luqm6uty.auth0.com/api/v2/users/'
    fuser += pageProps.user.user_id
     await fetch(fuser, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'authorization': bearer
        },
        credentials: "include"
      })
      .then((response) => response.json() )
      .then( function(jsonr) {
        if(jsonr.status >= 400)
        {
          throw new Error('Bad Response from server: ' + jsonr.status + ' ' + jsonr.statusText)
        }
      
       pageProps.user = jsonr;
      
      })
    }
    
   

    return { pageProps };
  
  }

  constructor(props) {
    super(props);
  
    this.state = {
      user: props.pageProps.user
    };
  }

  render() {
    const { Component, pageProps } = this.props;

    const props = {
      ...pageProps,
      user: this.state.user,
    };

    return (
      <NextContainer>
        <Head>
          <title>TutorKwik | Study with a Button</title>
         
          <meta name="viewport" content="width=device-width, initial-scale=1"/>   
        </Head>
        <Navbar user={this.state.user} />
            <Component {...props} />
      </NextContainer>
    );
  }
}

export default MyApp;
