import styled from "styled-components";
import React from 'react'
import Link from 'next/link'

const Picture = styled.img`
border-radius: 50%;
border: 3px solid white;
width: 100px;
`;

class Profile extends React.Component
{
  static async getInitialProps({ Component, ctx }) 
  {
    
    
    console.log(this.props.user);
     var bearer = 'Bearer ' + "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6Ik5FUkRPREE1TlRCRk5UTkdRMFUwTVRVek1qaEZPVVF3T0RkRFFUTTVOa1ZGTlRaRk56azVSZyJ9..hHcZ45TqSpqvBGZSIWPzdfsvRqSG_oXZ0D2ebjq9OIAGAzIMeN4_nambcDmle-z29OgJETiaT7etP-Vg5IZMXPlwzbYRt99pa_mWdVubrU8SVD4vic9RpOGF1mJ54mzkf9gcN12wRWdjLKdzCz0AJvetpoDWMiODmaAuhWUImh62rmKjFvOTW-f-LL6tZAymm_mu8eME5L2JSNrGt8eLtA7S3knXS5CLgPsa7cefmLKtbQ79UM2DMLQtrE3H43reR9bNjRqsu7JdQffAPgMB1ozDHcmiIrSVPhLyEjZVz-aOxqoPtMdI89wdRt_G140kNvixbZG_Bc669oKkQiCDwg";
      console.log(bearer)
      var fuser = 'https://dev-luqm6uty.auth0.com/api/v2/users/'
      fuser += this.props.user.user_id
        fetch(fuser, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'authorization': bearer
          },
          credentials: "include"
        })
        .then((response) => response.json() )
        .then( function(jsonr) {
          if(jsonr.status >= 400)
          {
            throw new Error('Bad Response from server: ' + jsonr.status + ' ' + jsonr.statusText)
          }
         this.props.user = jsonr
         console.log(this.props.user);
        })
        return jsonr
  }

  constructor(props)
  {
    super(props);
  }

  render()
  {
      return(
      <div>
        <div className="alert alert-success" role="alert">
          <Link href='/tutor_signup'>
          <a>Sign up to be a Tutor Today! Click on this link to apply</a>
          </Link>
        </div>
        <div className="alert alert-warning" role="alert">
         You haven't verified your account <Link href='/send_verify'><a>press this link to receive your verification email</a></Link>
        </div>
        
        <h2>
          <Picture src={this.props.user.picture} alt={this.props.user.displayName} /> Hello, {this.props.user.displayName}
        </h2>
        <Link href="/profile_edit">
          <input type='button' value='Edit profile'  />
        </Link>
        <p>This is what we know about you:</p>
        <ul>
          { Object.keys(this.props.user).map(key => (
            <li key={key}>{key}: {this.props.user[key].toString()}</li>
          ))}
        </ul>
        
      </div>   
      )
  }
}

export default Profile;
