import styled from "styled-components";
import { BraintreeHostedFields } from 'braintree-web-react';
import FormValidator from '../formValidator'

const Picture = styled.img`
border-radius: 50%;
border: 3px solid white;
width: 100px;
`;


class ProfileEdit extends React.Component {
    constructor(props) {
        super(props);
        this.state =
            {
                clientToken: null,
                email: this.props.user.email,
                payment: this.props.user.user_metadata,
                picture: this.props.user.picture,
                description: this.props.user.user_metadata,
                name: this.props.user.displayName
            };

        this.submitted = false;

        this.validator = new FormValidator
            (
                [
                    {
                        field: 'email',
                        method: 'isEmpty',
                        validWhen: false,
                        message: 'Email is required.'
                    },
                    {
                        field: 'email',
                        method: 'isEmail',
                        validWhen: true,
                        message: 'That is not a valid email.'
                    },
                    {
                        field: 'name',
                        method: 'isEmpty',
                        validWhen: 'false',
                        message: 'Please enter your name'
                    },
                ]
            )

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (e) => {
        e.preventDefault();
        this.setState({ [e.target.name]: e.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();

        const validation = this.validator.validate(this.state);
        this.setState({validation});
        this.submitted = true;

        if (validation.isValid) 
        {
            
            fetch('https://dev-luqm6uty.auth0.com/api/v2/users/${this.props.user.user_id}',
            {
                method: 'POST',
                headers:
                {
                    'Content-Type': 'application/json',
                    'authorization': 'Bearer ' + "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6Ik5FUkRPREE1TlRCRk5UTkdRMFUwTVRVek1qaEZPVVF3T0RkRFFUTTVOa1ZGTlRaRk56azVSZyJ9..Iakp1I4gf8epO8AJiAjuMLTMv5sfcPNqIPR3juEjaolsUbB2XFQPUlKnztQsQ8aD_lAfmEVe0Prg3umLszM0LVXXEBWHhPka4lGQD6KWJazEX1_iNscjFFyRRrR5PFBC4tGELJ3O3WmC4XO6-TdzdTUllU99Nnx3tkV_LFOsijDnDeAAaMAcnp658NlVQip2Eh7_b6gHyXgli-oGjnE9r4Zkkqh4pFxfZFcKS-9QCCfvud7fFRa0j917ay6_IVYXye_5DbuLbgNQtoubGcGPmNYLNpj9jNFuY39fDb_kpNh4zhoIKlo0nc4aJSU6mXisxBKbL-H_Nqj4sujcPlXStQ"
                },
                body: {
                    "email": this.state.email,
                    "user_metadata": {
                        'braintree': this.state.payment,
                        'description': this.state.description,
                    },
                    'name': this.state.name,
                    'picture': this.state.picture,

                },
                credentials: "include"
            }).then(console.log('tried'))

        }
       
    }

    async componentDidMount() {

        try {
            // Get a client token for authorization from your server
            const response = await fetch('http://localhost:8000/api/braintree/v1/getToken')
            const clientToken = response.data.clientToken

            this.setState({ clientToken })
            console.log(this.state.clientToken)
        } catch (err) {
            console.error(err)
        }
    }

    render() {
        let validation = this.submitted ?                        
                      this.validator.validate(this.state) :   
                      this.state.validation    

        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Profile Picture:</label>
                    <button onClick={(e) => { }}><Picture src={this.state.picture} alt={this.state.name} /> </button>

                    <label className='center-block'>Display Name:</label>
                    <input type='text' placeholder='Name' value={this.state.name} onChange={this.handleChange} />

                    <label>Email:</label>
                    <input type='email' placeholder='Email' value={this.state.email} onChange={this.handleChange} />

                    <label> Description: </label>
                    <input type='text' placeholder='Description' value={this.state.description} onChange={this.handleChange} />

                    <label>Subjects Taught:</label>

                    <label> Payment Info: </label>
                    <BraintreeHostedFields
                        className="drop-in-container"
                        options={{
                            authorization: this.state.clientToken
                        }}
                        onInstance={(instance) => (this.instance = instance)}
                    >
                        <form id="cardForm">
                            <label className="hosted-fields--label">Card Number</label>
                            <div id="card-number" className="hosted-field"></div>

                            <label className="hosted-fields--label">Expiration Date</label>
                            <div id="expiration-date" className="hosted-field"></div>

                            <label className="hosted-fields--label">CVV</label>
                            <div id="cvv" className="hosted-field"></div>

                            <label className="hosted-fields--label">Postal Code</label>
                            <div id="postal-code" className="hosted-field"></div>
                        </form>
                    </BraintreeHostedFields>

                    <input class="btn btn-dark" type='submit' value='Save Changes' />

                </form>
            </div>
        )
    }


}

export default ProfileEdit;
