import React from "react";
import App, { Container as NextContainer } from "next/app";
import Head from "next/head";
//import Container from "react-bootstrap/Container";
//import Jumbotron from "react-bootstrap/Jumbotron";
import Navbar from "../components/Navbar";
import fetch from 'isomorphic-fetch'

class MyApp extends App {
  static async getInitialProps({ Component, ctx }) 
  {
    
    let pageProps = {};
    if (Component.getInitialProps) 
    {
      pageProps = await Component.getInitialProps(ctx);
    }
    if (ctx.req && ctx.req.session.passport)
    {
      pageProps.user = ctx.req.session.passport.user;
      

      var bearer = 'Bearer ' + "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6Ik5FUkRPREE1TlRCRk5UTkdRMFUwTVRVek1qaEZPVVF3T0RkRFFUTTVOa1ZGTlRaRk56azVSZyJ9..TGeP6ob-kJKaikjU9ULF5JnNK1AVSN-Fx2PbhvyBXPQWw7oflgPcWlenahP5QBIn_zMA6D8N4-RGqDiGGT5ruLrnkr5940TaxQfo8laGolJPNDteVevtLl6RIaI84o2ppNe3uJV9_BCz66LjaoqWDIo2mx7S3t5YX6zZvBNrQbpVXyQwXMuHOFWz39IMv7QHcOto0do77YHL21Ka2wBx66NVBT_bv25_t0ePuQ1kM4IP9pA32zMRN9gIJ2ZtuA58ScKzC3s_J8-C-3IlTTVKx1xvs-CEuh_FExv4Y47c5v_-oz5dyBJ0r4Hj-l7HmT8zWCH4bPkGDjFkXJYZiJMYbA";
      console.log(bearer)
      var fuser = 'https://dev-luqm6uty.auth0.com/api/v2/users/'
      fuser += pageProps.user.user_id
        fetch(fuser, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'authorization': bearer
          },
          credentials: "include"
        })
        .then((response) => response.json() )
        .then( response => {
          if(response.status >= 400)
          {
            throw new Error('Bad Response from server: ' + response.status + ' ' + response.statusText)
          }
          console.log(response)
          return response.json()
         // pageProps.user = response.json();
          
        })
        .then(function(myJson) {
          console.log(JSON.stringify(myJson));
        });
       
        
    }
    return { pageProps };
  
  }

  constructor(props) {
    super(props);
    this.state = {
      user: props.pageProps.user
    };
  }

  render() {
    const { Component, pageProps } = this.props;

    const props = {
      ...pageProps,
      user: this.state.user,
    };

    return (
      <NextContainer>
        <Head>
          <title>TutorKwik | Study with a Button</title>
          <link rel="stylesheet" href="https://bootswatch.com/4/lux/bootstrap.min.css"/>
          <meta name="viewport" content="width=device-width, initial-scale=1"/>
        </Head>
        <Navbar user={this.state.user} />
            <Component {...props} />
      </NextContainer>
    );
  }
}

export default MyApp;
